import React, { Component } from 'react'
import { Row, Col } from 'antd';
class HowItWorks extends Component {
    constructor(props){
        super(props);
        this.state = {
            steps : [
                {
                   image :'set',
                   button :'set',
                   title: 'Create a Style Profile',
                   description :'set'
                },
                {
                    image :'set',
                    button :'set',
                    title: 'Subscribe to VIP',
                    description :'set'
                 },
                 {
                    image :'set',
                    button :'set',
                    title: 'Dress up',
                    description :'set'
                 },
                 {
                    image :'set',
                    button :'set',
                    title: 'Keep or Swap',
                    description :'set'
                 }
            ]
        }
    }
    render() {
        const renderSteps = this.state.steps;
        const renderState = renderSteps.map(steps => 
            <Col xs={24} sm={24} md={12} lg={6} className="padding-15" >
               <h1> {steps.title}</h1>
               <h1> {steps.image}</h1>
               <h1> {steps.button}</h1>
               <h1> {steps.description}</h1>
            </Col>
            
            )
        return (
            <div className="col-24 sections">
                <h1 className='title'>HOW IT WORKS</h1>
                <Row>
                    {renderState}
                </Row>

            </div>
        )
    }
}

export default HowItWorks
