import React, { Component } from 'react'
import { Row, Col } from 'antd';
class Content extends Component {
    constructor(props){
        super(props);
        this.state = {
            subscribes : [
                {
                   key : 1,
                   image :'https://lynagails-caters.s3-ap-southeast-1.amazonaws.com/uploads/VIP_How-it-works_1.png',
                   title: 'Refresh your look',
                   description :'Think of StyleGenie VIP as your ultimate rotating closet! You can invigorate your wardrobe & discover new favourites. Be ready for anything on your calendar.'
                },
                {
                    key : 2,
                    image :'https://lynagails-caters.s3-ap-southeast-1.amazonaws.com/uploads/VIP_How-it-works_2.png',
                    title: 'Experiment with your style',
                    description :"Experiment with your personal style without the burden of ownership. Your personal style is always evolving, so why should'nt your wardrobe be too?"                 },
                 {
                    key : 3,
                    image :'https://lynagails-caters.s3-ap-southeast-1.amazonaws.com/uploads/VIP_How-it-works_3.png',
                    title: 'Save money',
                    description :'Our original goal is to provide you with great value for money. Take back time spent shopping & let us cover the cleaning. Enjoy the freedom '
                 },
                 {
                    key : 4,
                    image :'https://lynagails-caters.s3-ap-southeast-1.amazonaws.com/uploads/VIP_How-it-works_4.png',
                    title: 'Dedicated stylist',
                    description :'Keep items you love & enjoy special discounts! OR swap with new items on the next delivery!'
                 },
                 {
                    key : 5,
                    image :'https://lynagails-caters.s3-ap-southeast-1.amazonaws.com/uploads/VIP_How-it-works_3.png',
                    title: 'Save wardrobe space',
                    description :'Try on new clothes and accessories every week!'
                 },
                 {
                    key : 6,
                    image :'https://lynagails-caters.s3-ap-southeast-1.amazonaws.com/uploads/VIP_How-it-works_3.png',
                    title: 'Minimal Impact',
                    description :'Try on new clothes and accessories every week!'
                 }
            ]
        }
    }
    render() {  
        const renderSubscribes = this.state.subscribes;
        const renderState = renderSubscribes.map(subscribe => 
            <Col xs={24} sm={12} md={8} lg={8}  key={subscribe.key} className="column-padding" style={{height:300}}>
               <h1 className="howItWorksTitle"> {subscribe.title}</h1>
               <p> {subscribe.description}</p>
            </Col>
            
            )      
        return (
            <div>
                <Row className="rowWidth">
                    {renderState}
                </Row> 
            </div>         
        )
    }
}
export default Content
